import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "ResetPassword",
  data() {
    return {
      labelPosition: 'right',
      YclResetPassword: {
        email: '',
        code: '',
        NewPassword: ''
      }
    };
  },
  methods: {
    LostLogin() {
      if (this.YclResetPassword.email == '') {
        this.$message({
          message: "邮箱不能为空",
          type: 'warning'
        });
      } else if (this.YclResetPassword.code == '') {
        this.$message({
          message: "邀请码不能为空",
          type: 'warning'
        });
      } else if (this.YclResetPassword.NewPassword == '') {
        this.$message({
          message: "新密码不能为空",
          type: 'warning'
        });
      } else {
        axios.post(`mu/resetPassword?email=${this.YclResetPassword.email}&code=${this.YclResetPassword.code}&newPassword=${this.YclResetPassword.NewPassword}`).then(res3 => {
          if (res3.status === 400) {
            this.$message({
              message: "服务器错误！",
              type: 'warning'
            });
          } else if (res3.status === 220) {
            this.$message({
              message: "邀请码不正确或者邀请码和真实姓名不匹配！",
              type: 'warning'
            });
          } else if (res3.status === 210) {
            this.$message({
              message: "邮箱地址不存在",
              type: 'warning'
            });
          } else if (res3.status === 200) {
            this.$message({
              message: "修改密码成功！",
              type: 'success'
            });
            this.$router.push({
              path: '/'
            });
          }
        });
      }
    }
  }
};
