import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  //搜索数据

  name: "UserManagement",
  data() {
    return {
      tableDataName: "",
      currentPage: 4,
      pageSize: 4,
      totalItems: 0,
      filterTableDataEnd: [],
      flag: false,
      labelPosition: 'right',
      ActivationCode: {
        code: '',
        name: '',
        isAdmin: ''
      },
      codeData: [],
      userData: []
    };
  },
  created() {
    axios.get(`mu/showActivationCode`).then(res => {
      if (res.status === 403) {
        this.$message({
          message: 'Unauthorized',
          type: 'warning'
        });
      } else if (res.status === 200) {
        this.codeData = res.data;
      }
    });
    this.totalItems = this.codeData.length;
    if (this.totalItems > this.pageSize) {
      for (let index = 0; index < this.pageSize; index++) {
        this.codeData.push(this.codeData[index]);
      }
    } else {
      this.codeData = this.codeData;
    }
  },
  methods: {
    handleSizeChange(val) {
      console.log(`每页 ${val} 条`);
      this.pageSize = val;
      this.handleCurrentChange(this.currentPage);
    },
    handleCurrentChange(val) {
      console.log(`当前页: ${val}`);
      this.currentPage = val;
      //需要判断是否检索
      if (!this.flag) {
        //tableDataBegin不能写成tableDataEnd，不然在没有进行搜索功能的时候，不能进行分页操作，数据丢失
        this.currentChangePage(this.codeData);
      } else {
        this.currentChangePage(this.filterTableDataEnd);
      }
    },
    //组件自带监控当前页码
    currentChangePage(list) {
      let from = (this.currentPage - 1) * this.pageSize;
      let to = this.currentPage * this.pageSize;
      this.tableDataEnd = [];
      for (; from < to; from++) {
        if (list[from]) {
          this.tableDataEnd.push(list[from]);
        }
      }
    },
    deleteRow(index, rows) {
      axios.delete(`mu/deleteUser?email=${rows.email}`).then(res => {
        if (res.status === 403) {
          this.$message({
            message: 'Unauthorized',
            type: 'warning'
          });
        } else if (res.status === 404) {
          this.$message({
            message: 'Not Found',
            type: 'warning'
          });
        } else if (res.status === 200) {
          this.$message({
            message: "删除用户成功！",
            type: 'success'
          });
          axios.get(`mu/showAllUser`).then(res => {
            if (res.status === 403) {
              this.$message({
                message: 'Unauthorized',
                type: 'warning'
              });
            } else if (res.status === 200) {
              this.userData = res.data;
            }
          });
        }
      });
    },
    clickData() {},
    lookUsers() {
      axios.get(`mu/showAllUser`).then(res => {
        if (res.status === 403) {
          this.$message({
            message: 'Unauthorized',
            type: 'warning'
          });
        } else if (res.status === 200) {
          this.userData = res.data;
        }
      });
    },
    lookCode() {
      axios.get(`mu/showActivationCode`).then(res => {
        if (res.status === 403) {
          this.$message({
            message: 'Unauthorized',
            type: 'warning'
          });
        } else if (res.status === 200) {
          this.codeData = res.data;
        }
      });
    },
    AddActivationCode() {
      if (this.$data.ActivationCode.code === '') {
        this.$message({
          message: '请输入激活码！',
          type: 'warning'
        });
      } else if (this.$data.ActivationCode.name === '') {
        this.$message({
          message: '请输入真实姓名！',
          type: 'warning'
        });
      } else if (this.$data.ActivationCode.isAdmin === '') {
        this.$message({
          message: '请输入权限！',
          type: 'warning'
        });
      } else {
        axios.put(`mu/addActivationCode?code=${this.$data.ActivationCode.code}&name=${this.$data.ActivationCode.name}&isAdmin=${this.$data.ActivationCode.isAdmin}`).then(res => {
          if (res.status === 403) {
            this.$message({
              message: 'Unauthorized',
              type: 'warning'
            });
          } else if (res.status === 200) {
            this.$message({
              message: '添加注册码成功！',
              type: 'success'
            });
            axios.get(`mu/showActivationCode`).then(res => {
              if (res.status === 403) {
                this.$message({
                  message: 'Unauthorized',
                  type: 'warning'
                });
              } else if (res.status === 200) {
                this.codeData = res.data;
              }
            });
          }
        });
      }
    }
  }
};
