import axios from "axios";
export default {
  //搜索数据
  data() {
    return {
      labelPosition: 'right',
      ActivationCode: {
        code: '',
        name: '',
        isAdmin: ''
      },
      codeData: [],
      userData: [],
      search: '',
      currentPage: 1,
      pagesize: 5,
      total: ""
    };
  },
  computed: {
    code_data() {
      let search = this.search;
      // 搜索功能
      if (search !== '') {
        let list = this.codeData().filter(data => !search || data.name.toLowerCase().includes(search.toLowerCase()) || data.name.toLowerCase().includes(search.toLowerCase()));
        let fenye = list.slice((this.currentPage - 1) * this.pagesize, this.currentPage * this.pagesize);
        this.total = fenye.length;
        return list, fenye;
      } else {
        this.total = this.codeData.length;
        let fenye = this.codeData.slice((this.currentPage - 1) * this.pagesize, this.currentPage * this.pagesize);
        return fenye;
      }
    }
  },
  created: function () {
    axios.get(`mu/showActivationCode`).then(res => {
      if (res.status === 403) {
        this.$message({
          message: 'Unauthorized',
          type: 'warning'
        });
      } else if (res.status === 200) {
        this.codeData = res.data;
      }
    });
  },
  name: "UserManagement",
  methods: {
    handleSizeChange(val) {
      this.pagesize = val;
      axios.get(`mu/showActivationCode`).then(res => {
        if (res.status === 403) {
          this.$message({
            message: 'Unauthorized',
            type: 'warning'
          });
        } else if (res.status === 200) {
          this.codeData = res.data;
        }
      });
    },
    handleCurrentChange(val) {
      this.currentPage = val;
      axios.get(`mu/showActivationCode`).then(res => {
        if (res.status === 403) {
          this.$message({
            message: 'Unauthorized',
            type: 'warning'
          });
        } else if (res.status === 200) {
          this.codeData = res.data;
        }
      });
    },
    deleteRow(index, rows) {
      axios.delete(`mu/deleteUser?email=${rows.email}`).then(res => {
        if (res.status === 403) {
          this.$message({
            message: 'Unauthorized',
            type: 'warning'
          });
        } else if (res.status === 404) {
          this.$message({
            message: 'Not Found',
            type: 'warning'
          });
        } else if (res.status === 200) {
          this.$message({
            message: "删除用户成功！",
            type: 'success'
          });
          axios.get(`mu/showAllUser`).then(res => {
            if (res.status === 403) {
              this.$message({
                message: 'Unauthorized',
                type: 'warning'
              });
            } else if (res.status === 200) {
              this.userData = res.data;
            }
          });
        }
      });
    },
    clickData() {},
    lookUsers() {
      axios.get(`mu/showAllUser`).then(res => {
        if (res.status === 403) {
          this.$message({
            message: 'Unauthorized',
            type: 'warning'
          });
        } else if (res.status === 200) {
          this.userData = res.data;
        }
      });
    },
    lookCode() {
      axios.get(`mu/showActivationCode`).then(res => {
        if (res.status === 403) {
          this.$message({
            message: 'Unauthorized',
            type: 'warning'
          });
        } else if (res.status === 200) {
          this.codeData = res.data;
        }
      });
    },
    AddActivationCode() {
      if (this.$data.ActivationCode.code === '') {
        this.$message({
          message: '请输入激活码！',
          type: 'warning'
        });
      } else if (this.$data.ActivationCode.name === '') {
        this.$message({
          message: '请输入真实姓名！',
          type: 'warning'
        });
      } else if (this.$data.ActivationCode.isAdmin === '') {
        this.$message({
          message: '请输入权限！',
          type: 'warning'
        });
      } else {
        axios.put(`mu/addActivationCode?code=${this.$data.ActivationCode.code}&name=${this.$data.ActivationCode.name}&isAdmin=${this.$data.ActivationCode.isAdmin}`).then(res => {
          if (res.status === 403) {
            this.$message({
              message: 'Unauthorized',
              type: 'warning'
            });
          } else if (res.status === 200) {
            this.$message({
              message: '添加注册码成功！',
              type: 'success'
            });
            axios.get(`mu/showActivationCode`).then(res => {
              if (res.status === 403) {
                this.$message({
                  message: 'Unauthorized',
                  type: 'warning'
                });
              } else if (res.status === 200) {
                this.codeData = res.data;
              }
            });
          }
        });
      }
    }
  }
};
