import axios from "axios";
export default {
  name: "UpdateChannel",
  data() {
    return {
      userinfo: {
        "id": "",
        "name": "",
        "type": "",
        "creator": "",
        "creatorEmail": "",
        "score": "",
        "due": ""
      },
      activeName: '',
      labelPosition: 'right',
      updateChannel: {
        name: '',
        type: '',
        creator: '',
        creatorEmail: '',
        score: '',
        due: ''
      },
      options: [{
        value: 'Thesis',
        label: '论文'
      }, {
        value: 'Project',
        label: '项目'
      }],
      value: ''
    };
  },
  created: function () {
    this.look = this.$route.query.row;
    axios.get(`mu/getChannelById?id=${this.look.id}`, {
      id: this.look.id
    }).then(res => {
      this.userinfo.id = res.data.id;
      this.userinfo.name = res.data.name;
      if (res.data.type === "Thesis") {
        this.userinfo.type = "论文";
      } else if (res.data.type === "Project") {
        this.userinfo.type = "项目";
      }
      this.userinfo.creator = res.data.creator;
      this.userinfo.creatorEmail = res.data.creatorEmail;
      this.userinfo.score = res.data.score;
      this.userinfo.due = res.data.due;
    });
  },
  methods: {
    parseTime(time, cFormat) {
      if (arguments.length === 0) {
        return null;
      }
      const format = cFormat || '{y}-{m}-{d} {h}:{i}:{s}';
      let date;
      if (typeof time === 'object') {
        date = time;
      } else {
        if (typeof time === 'string' && /^[0-9]+$/.test(time)) {
          time = parseInt(time);
        }
        if (typeof time === 'number' && time.toString().length === 10) {
          time = time * 1000;
        }
        date = new Date(time);
      }
      const formatObj = {
        y: date.getFullYear(),
        m: date.getMonth() + 1,
        d: date.getDate(),
        h: date.getHours(),
        i: date.getMinutes(),
        s: date.getSeconds(),
        a: date.getDay()
      };
      const time_str = format.replace(/{([ymdhisa])+}/g, (result, key) => {
        const value = formatObj[key];
        // Note: getDay() returns 0 on Sunday
        if (key === 'a') {
          return ['日', '一', '二', '三', '四', '五', '六'][value];
        }
        return value.toString().padStart(2, '0');
      });
      return time_str;
    },
    handleClick(tab, event) {
      console.log(tab, event);
    },
    lookChannel() {
      this.look = this.$route.query.row;
      this.$message({
        message: this.look,
        type: 'success'
      });
    },
    updateChannelNow() {
      if (this.$data.updateChannel.name === '') {
        this.$message({
          message: "请输入新的通道名称！",
          type: 'warning'
        });
      } else if (this.$data.updateChannel.type === '') {
        this.$message({
          message: "请输入新的通道类型！",
          type: 'warning'
        });
      } else if (this.$data.updateChannel.creator === '') {
        this.$message({
          message: "请输入新的通道创建者！",
          type: 'warning'
        });
      } else if (this.$data.updateChannel.creatorEmail === '') {
        this.$message({
          message: "请输入新的通道创建者邮箱！",
          type: 'warning'
        });
      } else if (this.$data.updateChannel.score === '') {
        this.$message({
          message: "请输入新的通道分数！",
          type: 'warning'
        });
      } else if (this.$data.updateChannel.due === '') {
        this.$message({
          message: "请输入新的通道截止日期！",
          type: 'warning'
        });
      } else {
        this.look = this.$route.query.row;
        this.due = this.$data.updateChannel.due.toString();
        this.$message({
          message: this.look.id,
          type: 'success'
        });
        axios.post(`mu/updateChannel?id=${this.look.id}&name=${this.$data.updateChannel.name}&type=${this.$data.updateChannel.type}&creator=${this.$data.updateChannel.creator}&creatorEmail=${this.$data.updateChannel.creatorEmail}&score=${this.$data.updateChannel.score}&due=${this.$data.updateChannel.due}`).then(res => {
          if (res.status === 400) {
            this.$message({
              message: "serve error",
              type: 'warning'
            });
          } else if (res.status === 403) {
            this.$message({
              message: "Unauthorized",
              type: 'warning'
            });
          } else if (res.status === 404) {
            this.$message({
              message: "Name Not Found",
              type: 'warning'
            });
          } else if (res.status === 200) {
            this.$message({
              message: res.data,
              type: 'success'
            });
          }
        });
      }
    }
  }
};
