  },
  methods: {
    edit() {
      let setTime = this.GetDateTime();
      console.log(setTime);
      const param = new FormData();
      this.fileListjpg.forEach(val => {
        param.append("fig", val.raw);
      });
      this.fileListzip.forEach(val => {
        param.append("zip", val.raw);
      });
      axios.post(`mu/thesis/update?id=${this.$store.state.thesis.id}&name=${this.$data.thesis.name}&uploaderEmail=${this.$store.state.userinfo.email}&maintainer=${this.$data.thesis.maintainer}&channelId=${this.$data.thesis.channelId}&description=${this.$data.thesis.description}&company=${this.$data.project.company}&money=${this.$data.project.money}&setTime=${setTime}&startYear=${this.$data.project.startYear}`, param).then(res => {
