import axios from "axios";
export default {
  data() {
    return {
      activeName: 'login',
      labelPosition: 'right',
      formLabelAlign: {
        email: '',
        password: ''
      },
      YclResetPassword: {
        email: '',
        code: '',
        NewPassword: ''
      },
      yclRegister: {
        username: '',
        phone: '',
        email: '',
        school: '',
        password: '',
        code: '',
        name: ''
      }
    };
  },
  methods: {
    handleClick(tab, event) {
      console.log(tab, event);
    },
    logout() {
      axios.get('mu/logout').then(res => {
        alert("注销登录成功");
        return res;
      });
    },
    login() {
      if (this.formLabelAlign.email == '') {
        //验证当用户输入的手机号为空或者不正确时不能进行获取验证码
        this.$message({
          message: '邮箱不能为空！',
          type: 'warning'
        });
      } else if (this.formLabelAlign.password == "") {
        this.$message({
          message: '密码不能为空！',
          type: 'warning'
        });
      } else {
        axios.get(`mu/login/${this.$data.formLabelAlign.email}/${this.$data.formLabelAlign.password}`, {
          email: this.$data.formLabelAlign.email,
          password: this.$data.formLabelAlign.password
        }).then(res2 => {
          if (res2.status === 400) {
            this.$message({
              message: "serve error",
              type: 'warning'
            });
          } else if (res2.status === 220) {
            this.$message({
              message: "wrong password",
              type: 'warning'
            });
          } else if (res2.status === 210) {
            this.$message({
              message: "can't find email",
              type: 'warning'
            });
          } else if (res2.status === 200) {
            this.$message({
              message: "login success",
              type: 'success'
            });
            axios.get(`mu/getUsernames/${this.$data.formLabelAlign.email}`, {
              email: this.$data.formLabelAlign.email
            }).then(res1 => {
              this.$message({
                message: res1.data,
                type: 'success'
              });
            });
          }
        });
      }
    },
    LostLogin() {
      if (this.YclResetPassword.email == '') {
        this.$message({
          message: "邮箱不能为空",
          type: 'warning'
        });
      } else if (this.YclResetPassword.code == '') {
        this.$message({
          message: "邀请码不能为空",
          type: 'warning'
        });
      } else if (this.YclResetPassword.NewPassword == '') {
        this.$message({
          message: "新密码不能为空",
          type: 'warning'
        });
      } else {
        axios.post(`mu/resetPassword?email=${this.YclResetPassword.email}&code=${this.YclResetPassword.code}&newPassword=${this.YclResetPassword.NewPassword}`).then(res3 => {
          if (res3.status === 400) {
            this.$message({
              message: "serve error",
              type: 'warning'
            });
          } else if (res3.status === 220) {
            this.$message({
              message: "Code is incorrect or not match",
              type: 'warning'
            });
          } else if (res3.status === 210) {
            this.$message({
              message: "Email not exists",
              type: 'warning'
            });
          } else if (res3.status === 200) {
            this.$message({
              message: "Reset password successfully",
              type: 'success'
            });
          }
        });
      }
    },
    register() {
      if (this.yclRegister.username == '') {
        this.$message({
          message: "用户名不能为空",
          type: 'warning'
        });
      } else if (this.yclRegister.phone == '') {
        this.$message({
          message: "电话号码不能为空",
          type: 'warning'
        });
      } else if (this.yclRegister.email == '') {
        this.$message({
          message: "邮箱不能为空",
          type: 'warning'
        });
      } else if (this.yclRegister.school == '') {
        this.$message({
          message: "学校不能为空",
          type: 'warning'
        });
      } else if (this.yclRegister.password == '') {
        this.$message({
          message: "密码不能为空",
          type: 'warning'
        });
      } else if (this.yclRegister.code == '') {
        this.$message({
          message: "邀请码不能为空",
          type: 'warning'
        });
      } else if (this.yclRegister.name == '') {
        this.$message({
          message: "真实姓名不能为空",
          type: 'warning'
        });
      } else {
        axios.post(`mu/register?username=${this.yclRegister.username}
        &phone=${this.yclRegister.phone}&email=${this.yclRegister.email}
        &school=${this.yclRegister.school}&password=${this.yclRegister.password}
        &code=${this.yclRegister.code}&name=${this.yclRegister.name}`).then(res4 => {
          if (res4.status === 400) {
            this.$message({
              message: "serve error",
              type: 'warning'
            });
          } else if (res4.status === 220) {
            this.$message({
              message: "Code is incorrect or not match",
              type: 'warning'
            });
          } else if (res4.status === 210) {
            this.$message({
              message: "Email already exists",
              type: 'warning'
            });
          } else if (res4.status === 200) {
            this.$message({
              message: res4.data,
              type: 'success'
            });
          }
        });
      }
    }
  }
};
